import './style.css';
import { useState } from 'react';
import axios from 'axios';

export default function MainContainer() {

    const selectedFile = useState();

    const fileSelectedHandler = (event) => {
        selectedFile(event.target.files[0]);
    }

    const fileUplaodHandler = () => {
        const fd = new FormData();
        fd.append('img', selectedFile, selectedFile.name);
        axios.post('http://127.0.0.1:5000/upload', fd);
    }

    return (
        <main>
      <div class="container-principal">
        <div class="content-container">
          <h1>Upload a photo â¤µ</h1>
          <input
            type="file"
            id="upload-button"
            class="input-upload"
            accept="image/png, image/jpeg"
            onChange={fileSelectedHandler}
          />
          <p class="p-scanner">
            Press the 'browse' button to upload the image you want to convert
            into a text file.
          </p>
          <button onClick={fileUplaodHandler}>
            Convert to text
          </button>
        </div>
      </div>
    </main>
    )
}
